// rename, be more precise...

exports.getOctets = function (obj) {

  var o  = obj.o
    , ko = 1024 * obj.ko
    , Mo = 1024 * 1024 * obj.Mo
    , Go = 1024 * 1024 * 1024 * obj.Go;

  return (o || 0)
       + (ko || 0)
       + (Mo || 0)
       + (Go || 0);

};

exports.getTs = function (obj) {

  var milliseconds  =  obj.milliseconds
    , seconds      =  1000 * obj.seconds
    , minutes      =  1000 * 60 * obj.minutes
    , hours        =  1000 * 60 * 60 * obj.hours
    , days         =  1000 * 60 * 60 * 24 * obj.days;

  return (milliseconds || 0)
       + (seconds || 0)
       + (minutes || 0)
       + (hours || 0)
       + (days || 0);

};

exports.ts = function (ts) {

  var date = new Date(ts);

  var minutes = date.getMinutes()
    , seconds = date.getSeconds()
    , milliseconds = date.getMilliseconds();

  return {
    "minutes": minutes,
    "seconds": seconds,
    "milliseconds": milliseconds
  };

};
